const items = $input.all();
const now = new Date();
const thirtyDaysFromNow = new Date(now.getTime() + 30 * 24 * 60 * 60 * 1000);

const processedDomains = items.map(item => {
  const data = item.json;
  let renewalStatus = 'unknown';
  let daysUntilExpiration = null;

  // Sanitize expirationDate - reject n8n expression strings
  let expirationDate = data.expirationDate || null;
  if (expirationDate && (expirationDate.includes('{{') || expirationDate.includes('$json'))) {
    expirationDate = null;
  }
  if (expirationDate) {
    const testDate = new Date(expirationDate);
    if (isNaN(testDate.getTime())) {
      expirationDate = null;
    }
  }

  if (expirationDate) {
    const expDate = new Date(expirationDate);
    daysUntilExpiration = Math.ceil((expDate - now) / (1000 * 60 * 60 * 24));

    if (expDate < now) {
      renewalStatus = 'expired';
    } else if (expDate < thirtyDaysFromNow) {
      renewalStatus = 'warning';
    } else {
      renewalStatus = 'healthy';
    }
  }

  // Map statusCategory - keep 'unknown' for unchecked domains
  const httpStatus = parseInt(data.httpStatus, 10) || 0;
  let statusCategory = data.statusCategory || 'unknown';
  if (httpStatus === 0 && (!statusCategory || statusCategory === 'down')) {
    statusCategory = 'unknown';
  }

  return {
    json: {
      id: data.id || data.domain,
      domain: data.domain,
      httpStatus: httpStatus,
      statusCategory: statusCategory,
      registrar: data.registrar || 'Unknown',
      nameServers: data.nameServers || '',
      expirationDate: expirationDate,
      createdDate: data.createdDate || null,
      lastChecked: data.lastChecked,
      cloudflareZoneId: data.cloudflareZoneId || '',
      renewalStatus: renewalStatus,
      daysUntilExpiration: daysUntilExpiration,
      createdAt: data.createdAt,
      updatedAt: data.updatedAt
    }
  };
});

return processedDomains;
